export const starter: "import { useState } from 'react';\n/** @jsx jsx */\nimport { css, jsx, Global } from '@emotion/react';\n\nconst Hello = () => {\n  const [name, setName] = useState('red');\n\n  return (\n    <div css={`color:${name}`}>\n      <h1>Hello World!</h1>\n      <label>\n        You can change the color:\n        <input\n          css={`margin: 12px`}\n          value={name}\n          onChange={(e) => setName(e.target.value)}\n        />\n      </label>\n    </div>\n  );\n};\n\nexport default () => (\n  <>\n    <Global\n      styles={css`\n      #zbody{\n        background: b\n          margin: 0;\n          overflow: overlay;\n        }  \n    `}\n    />\n    <Hello />\n  </>\n);";
